    private void jButtonajouterActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButtonajouterActionPerformed
        AnnonceDAO dao = new AnnonceDAO();
        Annonce a = new Annonce();
        
        
        a.setVilleArrive(jTextFieldvillea.getText());
        a.setVilleDepart(jTextFieldvilled.getText());
        a.setDate(jTextFielddate.getText());
        a.setHeure(Integer.parseInt(jTextFieldh.getText()));
        a.setMinute(Integer.parseInt(jTextFieldm.getText()));
        a.setNbPlace(Integer.parseInt(jTextFieldnb.getText()));
        a.setRefConducteur(Integer.parseInt(jTextFieldCond.getText()));
        dao.AjouterAnnonce(a);
        
    }//GEN-LAST:event_jButtonajouterActionPerformed

